import { StatusCodes } from "http-status-codes";
import bcrypt from "bcrypt";
import User from "../models/User.js";
import { generateToken } from "../helpers/authHelper.js";

/**
 * Controller method to insert a new user into the users collection
 * @param {*} req
 * @param {*} res
 */
export const createUser = async (req, res) => {
  try {
    //GETTING the hashed value from the password
    //SALT will be autogenerated for us and included
    const hashedPassword = await bcrypt.hash(req.body.password, 10);

    const createdUser = await User.create({
      firstName: req.body.firstName,
      lastName: req.body.lastName,
      userName: req.body.userName,
      email: req.body.email,
      password: hashedPassword,
    });

    return res
      .status(StatusCodes.OK)
      .json({ message: "User Created", createdUser });
  } catch (error) {
    return res
      .status(StatusCodes.INTERNAL_SERVER_ERROR)
      .json({ message: error.toString() });
  }
};
export const deleteUserById = async (req, res) => {
  try {
    const user = await User.findByIdAndDelete(req.params.id);

    if (!user) {
      return res.status(StatusCodes.NOT_FOUND).json("User not found");
    }

    return res
      .status(StatusCodes.OK)
      .json({ message: "User deleted", deletedUser: user });
  } catch (error) {
    return res
      .status(StatusCodes.INTERNAL_SERVER_ERROR)
      .json({ message: "Error happened", error: error.toString() });
  }
};
export const updateUser = async (req, res) => {
  try {
    const hashedPassword = await bcrypt.hash(req.body.password, 10);
    const user = await User.findByIdAndUpdate(
      req.params.id,
      {
        firstName: req.body.firstName,
        lastName: req.body.lastName,
        userName: req.body.userName,
        email: req.body.email,
        password: hashedPassword,
      },
      { new: true }
    );

    if (!user) {
      return res.status(StatusCodes.NOT_FOUND).json("User not found");
    }

    return res
      .status(StatusCodes.OK)
      .json({ message: "User updated", updatedUser: user });
  } catch (error) {
    return res
      .status(StatusCodes.INTERNAL_SERVER_ERROR)
      .json({ message: "Error happened", error: error.toString() });
  }
};

/**
 * Controller method to sign in a user
 * @param {*} req
 * @param {*} res
 */
export const loginUser = async (req, res) => {
  try {
    //do we have a user with that username?
    const user = await User.findOne({ userName: req.body.userName });

    if (!user) {
      return res
        .status(StatusCodes.UNAUTHORIZED)
        .json({ message: "User not authorized." });
    }

    const checkPassword = await bcrypt.compare(
      req.body.password,
      user.password
    );

    if (!checkPassword) {
      return res
        .status(StatusCodes.UNAUTHORIZED)
        .json({ message: "User not authorized." });
    }
    //generate our token
    const token = generateToken(user);

    return res
      .status(200)
      .cookie("jwt", token, { httpOnly: true, sameSite: "lax", secure: false })
      .json({
        message: "Login successful",

        user: { username: user.userName },
      });
  } catch (error) {
    res.status(StatusCodes.BAD_REQUEST).send(error.message);
  }
};

export const logoutUser = (request, response) => {
  response
    .clearCookie("jwt", {
      httpOnly: true,
      sameSite: "lax",
      secure: false,
    })
    .send("User is logged out");
};
